import React, { Component } from "react";
import { getWorshippers } from "../common/Worshippers";
import { toast } from "react-toastify";

class Confimed extends Component {
  state = { worshipper: [] };
  async componentDidMount() {
    try {
      const { data: worshipper } = await getWorshippers(
        this.props.match.params.id
      );
      this.setState({ worshipper });
    } catch (ex) {
      if (ex.response && ex.response.status === 400)
        toast.error(ex.response.data);
    }
  }
  render() {
    return (
      <div>
        <div className="jumbotron text-center">
          <h1 className="display-7">SEAT BOOKED</h1>
          <p className="lead">
            <strong>Please check your email</strong> and click on the link sent
            to your email to finish booking your seat
          </p>
          <hr></hr>
          <p>
            To delete your booking press here:
            <button className="btn btn-danger">DELETE</button>
          </p>
        </div>
      </div>
    );
  }
}

export default Confimed;
