import React, { Component } from "react";
import Form from "../common/form";
import Joi from "joi-browser";
import Services from "../common/testing";
import { Link } from "react-router-dom";

class MainPage extends Form {
  state = {
    data: {
      firstname: "",
      lastname: "",
      email: "",
      phonenumber: "",
      dob: "",
      service: {},
    },
    errors: {},
  };
  handleServicesSelect = (services) => {
    const data = { ...this.state.data };
    data[services] = services;
    console.log(services);
    this.setState({ data });
    this.setState({ selectedService: services });
  };
  services = {};
  schema = {
    firstname: Joi.string()
      .min(3)
      .max(20)
      .required()
      .label("Firstname")
      .error(() => {
        return {
          message: "Enter a valid FirstName",
        };
      }),
    lastname: Joi.string()
      .min(3)
      .max(20)
      .required()
      .label("Lastname")
      .error(() => {
        return {
          message: "Enter a valid LastName",
        };
      }),
    email: Joi.string()
      .email()
      .label("Email")
      .error(() => {
        return {
          message: "Enter a valid Email",
        };
      }),
    phonenumber: Joi.number()
      //   .min(0700000000)
      //   .max(0800000000)
      .label("Phone Number")
      .error(() => {
        return {
          message: "Enter a valid phone number",
        };
      }),
    dob: Joi.number()
      .label("Date of Birth")
      .error(() => {
        return {
          message: "Enter a valid Email",
        };
      }),
    service: Joi.string(),
  };
  doSubmit = () => {
    console.log("submitted");
  };
  render() {
    return (
      <div>
        <div className="m-2">
          <h6 style={{ textAlign: "center" }}>
            ACK ST PETERS KAHAWA SUKARI <br /> SUNDAY SERVICE BOOKING <br />
          </h6>
          Kindly fill the form to reserve your seat from the comfort of your
          home
        </div>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row">
            <div className="col">
              {this.renderInput("firstname", "First Name")}
            </div>
            <div className="col">
              {this.renderInput("lastname", "Last Name")}
            </div>
          </div>
          {this.renderInput("email", "Email", "email")}
          <div className="form-row">
            <div className="col">
              {this.renderInput("phonenumber", "Phone Number")}
            </div>
            <div className="col">
              {this.renderInput("dob", "Year of Birth")}
            </div>
          </div>
          {this.renderInput("service", "Service")}
          <Services
            onTry={this.state.data.service}
            selectedItem={this.state.selectedService}
            onItemSelect={this.handleServicesSelect}
          />
          <Link to="/Booked">{this.renderButton("BOOK")}</Link>
        </form>
      </div>
    );
  }
}
export default MainPage;
