import React, { Component } from "react";
import Form from "../common/form";
import Joi from "joi-browser";

class MainPage extends Form {
  state = {
    data: {
      firstname: "",
      lastname: "",
      email: "",
      phonenumber: "",
      dob: "",
      service: "",
    },
    errors: {},
  };
  schema = {
    firstname: Joi.string()
      .min(3)
      .max(20)
      .required()
      .label("Firstname")
      .messages({
        "string.base": `"firstname" should be a type of 'text'`,
        // "string.empty": `"a" cannot be an empty field`,
        // "string.min": `"a" should have a minimum length of {#limit}`,
        // "any.required": `"a" is a required field`,
      }),
    lastname: Joi.string().min(3).max(20).required().label("Lastname"),
    email: Joi.string().email().label("Email"),
    phonenumber: Joi.number().label("Phone Number"),
    dob: Joi.number().label("Date of Birth"),
    service: Joi.string(),
  };
  doSubmit = () => {
    console.log("submitted");
  };
  render() {
    return (
      <div>
        <h1>Form</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row">
            <div className="col">
              {this.renderInput("firstname", "First Name")}
            </div>
            <div className="col">
              {this.renderInput("lastname", "Last Name")}
            </div>
          </div>
          {this.renderInput("email", "Email", "email")}
          {this.renderInput("phonenumber", "Phone Number")}
          {this.renderInput("dob", "Date of Birth")}
          {this.renderInput("service", "Service")}
          {this.renderButton("BOOK")}
        </form>
      </div>
    );
  }
}
//email
//phone number
//date of birth
//service
export default MainPage;
