import React from "react";

const SidePanel = (props) => {
  const { items, onItemSelect, selectedItem, toDelete, toPrint } = props;
  return (
    <div>
      <ul className="list-group">
        {items.map((item) => (
          <li
            onClick={() => onItemSelect(item)}
            key={item._id}
            className={
              item === selectedItem
                ? "list-group-item active d-flex justify-content-between align-items-center"
                : "list-group-item d-flex justify-content-between align-items-center"
            }
          >
            {item.name}
            <span className="badge badge-primary badge-pill m-2">
              {item.seatsLeft}
            </span>
            <button
              className="btn btn-secondary m-1"
              style={{ fontSize: "14px" }}
              onClick={() => toDelete(item)}
            >
              Print
            </button>
            <button
              className="btn btn-danger m-1"
              onClick={() => toDelete(item)}
              style={{ fontSize: "12px" }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      <button className="btn btn-primary m-2">Add Service</button>
    </div>
  );
};

export default SidePanel;
