import React, { Component } from "react";
import { getService } from "../common/fakeServices";

class Services extends Component {
  state = { service: getService(), data: {}, errors: {} };
  handleChange = ({ currentTarget: input }) => {
    const errors = { ...this.state.errors };
    const errorMessage = this.validateProperty(input);
    if (errorMessage) errors[input.name] = errorMessage;
    else delete errors[input.name];

    const data = { ...this.state.data };
    data[input.name] = input.value;
    this.setState({ data, errors });
  };
  validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const schema = { [name]: this.schema[name] };
    const { error } = Joi.validate(obj, schema);
    return error ? error.details[0].message : null;
  };
  // handleClick = (propss) => {
  //   const service = { ...propss };
  //   console.log(service.service.name);
  // };
  render() {
    const { service } = this.state;
    console.log(this.props);
    return (
      <div className="row container p-2 d-flex justify-content-center">
        {service.map((service) => (
          <div className="col-sm-" key={service._id}>
            <div
              className="card text-center page-item active m-2"
              style={{ cursor: "pointer" }}
              href="#"
              value={service.name}
              name={service.name}
              onClick={() => this.handleChange()}
            >
              <div className="card-body page-link">
                <h5 className="card-title">{service.name}</h5>
                <p className="card-text">
                  Time: {service.time} <br /> Seats Left:
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Services;
