import React, { Component } from "react";
import Maintable from "../common/maintable";
import SidePanel from "../common/sidepanel";
import { getWorshippers } from "../common/fakeWorshippers";
import { getService } from "../common/Service";

class Dashboard extends Component {
  state = { services: [], worshippers: getWorshippers() };
  handleServicesSelect = (service) => {
    this.setState({ selectedService: service });
  };
  async componentDidMount() {
    const { data } = await getService();
    const service = [...data];
    this.setState({ service });
  }
  render() {
    // const { selectedService, services } = this.state;
    // const filtered = selectedService
    //   ? services.filter((m) => m.services._id === selectedService._id)
    //   : services;
    return (
      <React.Fragment>
        <main className="container">
          <div>
            <h4>DASHBOARD</h4>
            <p>Showing bookings</p>
            <div className="container">
              <div className="row">
                <div className="col-m">
                  <SidePanel
                    items={this.state.services}
                    selectedItem={this.state.selectedService}
                    onItemSelect={this.handleServicesSelect}
                  />
                </div>
                <div className="col-m">
                  <Maintable items={this.state.worshippers} />
                </div>
              </div>
            </div>
          </div>
        </main>
      </React.Fragment>
    );
  }
}

export default Dashboard;
