import React, { Component } from "react";
import Maintable from "../common/maintable";
import SidePanel from "../common/sidepanel";
import {
  getWorshippers,
  deleteWorshippers,
  printService,
} from "../common/Worshippers";
import { getService, deleteService } from "../common/Service";
import { toast } from "react-toastify";

class Dashboard extends Component {
  state = { service: [], worshippers: [] };

  async componentDidMount() {
    const { data: service } = await getService();
    const { data: worshippers } = await getWorshippers();
    this.setState({ service, worshippers });
  }
  handleServicesSelect = (service) => {
    this.setState({ selectedService: service });
  };

  handleDeleteService = async (services) => {
    const originalService = this.state.service;
    const service = originalService.filter((s) => s._id !== services._id);
    this.setState({ service });

    try {
      await deleteService(services._id);
    } catch (ex) {
      if (ex.response && ex.response.status === 404)
        toast.error("This Service has been already deleted");

      this.setState({ service: originalService });
    }
  };

  handleDeleteWorshippers = async (worshipper) => {
    const originalWorshipper = this.state.worshippers;
    const worshippers = originalWorshipper.filter(
      (s) => s._id !== worshipper._id
    );
    this.setState({ worshippers });

    try {
      await deleteWorshippers(worshipper._id);
    } catch (ex) {
      if (ex.response && ex.response.status === 404)
        toast.error("This person has been already deleted");

      this.setState({ worshippers: originalWorshipper });
    }
  };

  handlePrintService = async (print) => {
    try {
      await printService(print._id);
    } catch (ex) {
      if (ex.response && ex.response.status === 400)
        toast.error(ex.response.data);
    }
  };

  render() {
    // const { selectedService, services } = this.state;
    // const filtered = selectedService
    //   ? services.filter((m) => m.services._id === selectedService._id)
    //   : services;
    return (
      <React.Fragment>
        <div>
          <h4>DASHBOARD</h4>
          <p>Showing {this.state.service.length} service bookings</p>
          <SidePanel
            items={this.state.service}
            selectedItem={this.state.selectedService}
            onItemSelect={this.handleServicesSelect}
            toDelete={this.handleDeleteService}
            toPrint={this.handlePrintService}
          />
          {/* <div className="col-m">
                  <Maintable
                    items={this.state.worshippers}
                    toDelete={this.handleDeleteWorshippers}
                  />
                </div> */}
        </div>
      </React.Fragment>
    );
  }
}

export default Dashboard;
