import React, { Component } from "react";
import { getService } from "../common/fakeServices";
import Form from "./form";

class Services extends Form {
  state = { service: getService(), data: {}, errors: {} };

  render() {
    const { service } = this.state;
    const { onItemSelect, selectedItem } = this.props;

    return (
      <div className="row container p-2 d-flex justify-content-center">
        {service.map((service) => (
          <div className="col-sm-" key={service._id}>
            <div
              className={
                service === selectedItem
                  ? "card text-center page-item active m-2"
                  : "card text-center page-item  m-2"
              }
              style={{ cursor: "pointer" }}
              href="#"
              value={service.name}
              name={service.name}
              onClick={() => onItemSelect(service)}
            >
              <div className="card-body page-link">
                <h5 className="card-title">{service.name}</h5>
                <p className="card-text">
                  Time: {service.time} <br /> Seats Left:
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Services;
