import React, { Component } from "react";
import Form from "../common/form";
import Joi from "joi-browser";

class MainPage extends Form {
  state = {
    data: {
      firstname: "",
      lastname: "",
      email: "",
      phonenumber: "",
      dob: "",
      service: "",
    },
    errors: {},
  };
  schema = {
    firstname: Joi.string()
      .min(3)
      .max(20)
      .required()
      .label("Firstname")
      .error(() => {
        return {
          message: "Enter a valid FirstName",
        };
      }),
    lastname: Joi.string()
      .min(3)
      .max(20)
      .required()
      .label("Lastname")
      .error(() => {
        return {
          message: "Enter a valid LastName",
        };
      }),
    email: Joi.string()
      .email()
      .label("Email")
      .error(() => {
        return {
          message: "Enter a valid Email",
        };
      }),
    phonenumber: Joi.number()
      //   .min(0700000000)
      //   .max(0800000000)
      .label("Phone Number")
      .error(() => {
        return {
          message: "Enter a valid phone number",
        };
      }),
    dob: Joi.number()
      .label("Date of Birth")
      .error(() => {
        return {
          message: "Enter a valid Email",
        };
      }),
    service: Joi.string(),
  };
  doSubmit = () => {
    console.log("submitted");
  };
  render() {
    return (
      <div>
        <h1>Form</h1>
        <form onSubmit={this.handleSubmit}>
          <div className="form-row">
            <div className="col">
              {this.renderInput("firstname", "First Name")}
            </div>
            <div className="col">
              {this.renderInput("lastname", "Last Name")}
            </div>
          </div>
          {this.renderInput("email", "Email", "email")}
          <div className="form-row">
            <div className="col">
              {this.renderInput("phonenumber", "Phone Number")}
            </div>
            <div className="col">
              {this.renderInput("dob", "Year of Birth")}
            </div>
          </div>
          {this.renderInput("service", "Service")}
          {this.renderButton("BOOK")}
        </form>
      </div>
    );
  }
}
//email
//phone number
//date of birth
//service
export default MainPage;
